// http://www.ietf.org/rfc/rfc3986.txt ยง 5.2.4
var index = undefined;
var input = "" + path;
var output = [];
while (input) {
	// A
	index = input.indexOf("../");
	if (index == 0) {
		input = input.slice(3);
		continue;
	}
	index = input.indexOf("./");
	if (index == 0) {
		input = input.slice(2);
		continue;
	}
	// B
	index = input.indexOf("/./");
	if (index == 0) {
		input = "/" + input.slice(3);
		continue;
	}
	index = input.indexOf("/.");
	if ((index == 0) && (input.length == 2)) {
		input = "/" + input.slice(2);
		continue;
	}
	// C
	index = input.indexOf("/../");
	if (index == 0) {
		input = "/" + input.slice(4);
		if (output.length) {
			output.pop();
		}
		continue;
	}
	index = input.indexOf("/..");
	if ((index == 0) && (input.length == 3)) {
		input = "/" + input.slice(3);
		if (output.length) {
			output.pop();
		}
		continue;
	}
	// D
	index = input.indexOf("..");
	if (index == 0) {
		input = input.slice(2);
		continue;
	}
	index = input.indexOf(".");
	if (index == 0) {
		input = input.slice(1);
		continue;
	}
	// E
	index = input.indexOf("/", input.charAt(0) == "/" ? 1 : 0);
	if (index == -1) {
		output.push(input);
		input = "";
	}
	else {
		output.push(input.slice(0, index));
		input = input.slice(index);
	}
}
return output.join("");

var search = path.split("/");
var value = undefined;
if (search.length && !search[0]) {
	search.shift();
	var root = search.shift();
	if (root != node.localName) {
		node = undefined;
	}
	else if (!search.length) {
		value = node;
	}
}
search.forEach(function (item, index) {
	if (item && node) {
		if (item == "#text") {
			node = node.firstChild;
			if (node && (node.nodeType == node.TEXT_NODE)) {
				value = node.nodeValue;
			}
			node = undefined;
		}
		else if (item.charAt(0) == "@") {
			value = node.getAttribute(item.slice(1));
			node = undefined;
		}
		else {
			if (ns) {
				node = node.getElementNodeNS(ns, item);
			}
			else {
				node = node.getElementNode(item);
			}
			if (index == (search.length - 1)) {
				value = node;
				node = undefined;
			}
		}
	}
});
return value;

