			var time = ((h < 10) ? "0" + h : h) + ":" + ((m < 10) ? "0" + m : m) + ":" + ((s < 10) ? "0" + s : s) + (this.precision ? "." + ((ms < 10) ? "00" + ms : (ms < 100) ? "0" + ms : ms) : "");
