			activelock.appendChild(lockscope);
			if (exclusive) {
				lockscope.appendChild(document.createElementNS(this.davNamespace, "D:exclusive"));
			}
			else if (shared) {
				lockscope.appendChild(document.createElementNS(this.davNamespace, "D:shared"));
